import "./AddToBasketButton.css";
import { useState, useContext } from "react";
import AddToBasketIcon from "../Icons/AddToBasket";
import { OrderContext } from "Context/OrderContext";

export default function AddToBasketButton(props) {
  const cart = useContext(OrderContext);
  // const [isAdded, setIsAdded] = useState(false);

  return (
    <button
      className={`addToBasketButton ${cart.isItemInCart && "active"} ${
        props.className
      }`}
      onClick={() => cart.onAdd(props.item)}
    >
      <AddToBasketIcon />
    </button>
  );
}
