import "components/Buttons/Checkbox/Checkbox.css";

export default function Checkbox(props) {
  return (
    <li>
      <label className="checkbox">
        <input
          type="checkbox"
          defaultChecked={props.defaultChecked}
          value={props.children}
        />
        <span>{props.children}</span>
      </label>
    </li>
  );
}
