import "components/Buttons/Radio/RadioBtn.css";

export default function RadioBtn(props) {
  return (
    <label className="radio">
      <input
        type="radio"
        value={props.children}
        name={props.name}
        className="RadioBtn"
      />
      <span className={props.className}>{props.children}</span>
    </label>
  );
}
