export default function LastPrice({ price, currency, className }) {
  return (
    <div className={`last-price ${className}`}>
      <span>
        {new Intl.NumberFormat("ru-RU", {
          style: "currency",
          currency: currency,
          maximumFractionDigits: 0,
        }).format(price)}
      </span>
    </div>
  );
}
