import Star from "../../../Icons/Star/Star";

export default function Rating({ rate, disabled, onClick, className, id }) {
  return (
    <div className={className}>
      {Array(5)
        .fill("")
        .map((_, i) => (
          <button
            type="button"
            style={{ background: "none" }}
            disabled={disabled}
            key={i}
            onClick={() => onClick(i + 1)}
            id={`rate ${i + 1}`}
          >
            <Star className={Math.floor(rate) > i ? "yellow" : "gray"} />
          </button>
        ))}
    </div>
  );
}
