import { useState } from "react";

export const CustomTabs = ({ tabs, className }) => {
  const [activeTab, setActiveTab] = useState(0);

  const getActiveTab = () => tabs.find((_, index) => index === activeTab);

  return (
    <div>
      <div className={className}>
        {tabs.map((item, index) => (
          <button
            key={item.label}
            onClick={() => setActiveTab(index)}
            className={`tab ${index === activeTab && "active"}`}
          >
            {item.label}
          </button>
        ))}
      </div>
      <div className="tab-content">{getActiveTab().content}</div>
    </div>
  );
};

export default CustomTabs;
