import { useState } from "react";

import ArrowLeft from "../Icons/Arrow/ArrowLeft";
import ArrowRight from "../Icons/Arrow/ArrowRight";
import "./Stepper.css";

export default function StepperNum() {
  const [value, setValue] = useState(0);
  const maxSteps = 10;
  const minSteps = 0;

  const handlePlus = () => {
    setValue((value) => value + 1);
  };

  const handleMinus = () => {
    setValue((value) => value - 1);
  };
  return (
    <div className="stepper stepper__box">
      <button
        className="stepper__remove"
        disabled={minSteps === value}
        onClick={handleMinus}
      >
        <ArrowLeft className="stepper__switch" />
      </button>
      <div className="stepper__value">{value}</div>
      <button
        className="stepper__add"
        onClick={handlePlus}
        disabled={maxSteps === value}
      >
        <ArrowRight className="stepper__switch" />
      </button>
    </div>
  );
}
