import { useState, useContext } from "react";

import ArrowLeft from "../Icons/Arrow/ArrowLeft";
import ArrowRight from "../Icons/Arrow/ArrowRight";
import { OrderContext } from "Context/OrderContext";
import "./Stepper.css";

export default function StepperNum({
  quantity,
  id,
  handleMinus,
  handlePlus,
  qty,
}) {
  const cart = useContext(OrderContext);
  const maxSteps = 10;
  const minSteps = 1;

  return (
    <div className="stepper stepper__box">
      <button
        className="stepper__remove"
        disabled={minSteps === qty}
        onClick={handleMinus}
      >
        <ArrowLeft className="stepper__switch" />
      </button>
      <div className="stepper__value">{qty}</div>
      <button
        className="stepper__add"
        onClick={handlePlus}
        disabled={maxSteps === qty}
      >
        <ArrowRight className="stepper__switch" />
      </button>
    </div>
  );
}
