import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Thumbs } from "swiper";
import "swiper/swiper.scss";
import "swiper/components/thumbs/thumbs.scss";
import "./ProductSliderMain.css";

import InStock from "components/Catalog/CardProduct/InStock/InStock";
import CATALOG from "Consts/catalog/catalog";
import { isMobile } from "react-device-detect";
import SaleSign from "pages/product/components/SaleSign/SaleSign";

SwiperCore.use([Thumbs]);

export default function ProductSliderMain(props) {
  return (
    <Swiper
      spaceBetween={30}
      thumbs={props.thumbs}
      grabCursor={true}
      className={
        !isMobile ? "product__main-photo" : "product__main-photo mobile"
      }
    >
      <InStock
        className={!isMobile ? "product__instock" : "product__instock mobile"}
      />
      <SaleSign>-15%</SaleSign>

      {CATALOG[6].image.map((img, i) => (
        <SwiperSlide key={i}>
          <img src={img} alt="" />
        </SwiperSlide>
      ))}
    </Swiper>
  );
}
