import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Thumbs } from "swiper";
import "swiper/swiper.scss";
import "swiper/components/thumbs/thumbs.scss";
import "./ProductSliderThumbs.css";

import CATALOG from "Consts/catalog/catalog";

SwiperCore.use([Thumbs]);

export default function ProductSliderThumbs(props) {
  return (
    <Swiper
      onSwiper={props.onSwiper}
      spaceBetween={20}
      slidesPerView={5}
      className="product__preview-photo"
    >
      {props.item.image.map((img, i) => (
        <SwiperSlide key={i}>
          <img src={img} alt={props.item.title} />
        </SwiperSlide>
      ))}
    </Swiper>
  );
}
