import { isMobile } from "react-device-detect";

import CATALOG from "Consts/catalog/catalog";
import "./CharacteristicsTab.css";

export default function CharacteristicsTab(props) {
  const obj = CATALOG[6].characteristics;

  return (
    <>
      {!isMobile ? (
        <h4 className={`product__tab-title ${props.className}`}>
          Характеристики товара:
        </h4>
      ) : (
        <> </>
      )}
      <ul className="product__tab-characteristics-wrapper">
        {Object.entries(obj).map(([key, value]) => {
          return (
            <li key={key} className="product__tab-characteristics">
              <p>{value.key}</p>
              <p>{value.value}</p>
            </li>
          );
        })}
      </ul>
    </>
  );
}
